import React, { useState, useEffect, Fragment } from 'react';
import { Listbox, Transition } from '@headlessui/react';
import { CheckIcon, SelectorIcon } from '@heroicons/react/solid';

function classNames(...classes) {
  return classes.filter(Boolean).join(' ');
}

const defaultVal = [
  {
    id: 1,
    firstName: 'Select',
    lastName: 'Assignee',
    accountProfileUploaded:
      'data:image/svg+xml;base64,',
  },
];

const SelectOptions = ({ accounts }) => {
  const [selected, setSelected] = useState(defaultVal[0]);
  return (
    <>
      <Listbox value={selected} onChange={setSelected}>
        {({ open }) => (
          <>
            <div className='mt-1 relative'>
              <Listbox.Button className='w-full bg-white pl-3 py-4 text-left relative rounded-md border focus:border-primary focus:outline-none focus:shadow-outline cursor-default'>
                <span className='flex items-center'>
                  <img
                    src={selected.accountProfileUploaded}
                    alt=''
                    className='flex-shrink-0 h-6 w-6 rounded-full'
                  />
                  <span className='text-sm ml-3 block truncate'>
                    {`${selected.firstName} ${selected.lastName}`}
                  </span>
                </span>
                <span className='ml-3 absolute inset-y-0 right-0 flex items-center pr-2 pointer-events-none'>
                  <SelectorIcon
                    className='h-5 w-5 text-gray-400'
                    aria-hidden='true'
                  />
                </span>
              </Listbox.Button>

              <Transition
                show={open}
                as={Fragment}
                leave='transition ease-in duration-100'
                leaveFrom='opacity-100'
                leaveTo='opacity-0'
              >
                <Listbox.Options className='absolute z-10 mt-1 w-full bg-white shadow-lg max-h-56 rounded-md py-1 text-base ring-1 ring-black ring-opacity-5 overflow-auto focus:outline-none sm:text-sm'>
                  {defaultVal.map((account, index) => (
                    <Listbox.Option
                      key={index}
                      className={({ active }) =>
                        classNames(
                          active ? 'text-white bg-primary' : 'texttext-grey',
                          'cursor-default select-none relative py-2 pl-3 pr-9'
                        )
                      }
                      value={account}
                    >
                      {({ selected, active }) => (
                        <>
                          <div className='flex items-center'>
                            <img
                              src={account.accountProfileUploaded}
                              alt=''
                              className='flex-shrink-0 h-6 w-6 rounded-full'
                            />
                            <span
                              className={classNames(
                                selected ? 'font-semibold' : 'font-normal',
                                'ml-3 block truncate text-sm'
                              )}
                            >
                              {`${account.firstName} ${account.lastName}`}
                            </span>
                          </div>

                          {selected ? (
                            <span
                              className={classNames(
                                active ? 'text-white' : 'text-primary',
                                'absolute inset-y-0 right-0 flex items-center pr-4'
                              )}
                            >
                              <CheckIcon
                                className='h-5 w-5'
                                aria-hidden='true'
                              />
                            </span>
                          ) : null}
                        </>
                      )}
                    </Listbox.Option>
                  ))}
                </Listbox.Options>
              </Transition>
            </div>
          </>
        )}
      </Listbox>
    </>
  );
};

export default SelectOptions;
