import React, { useState, useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { Menu, Transition } from '@headlessui/react';
import { motion } from 'framer-motion';
import { CogIcon, ViewGridIcon, TrashIcon } from '@heroicons/react/outline';
import { NavLink } from 'react-router-dom';
import { logoutAccount } from '../redux/actions/accountActions';

var defaultAccountProfile =
  'data:image/svg+xml;base64,';

const SettingsModal = ({ profilePic, name, role = 'Admin', history }) => {
  const [toggleClass, setToggleClass] = useState(false);

  const dispatch = useDispatch();

  const handleToggleClass = () => {
    setToggleClass(!toggleClass);
  };

  const handleOnLogout = (e) => {
    e.preventDefault();
    dispatch(logoutAccount());
  };

  return (
    <>
      <div className='w-22 ml-auto'>
        <div className='flex items-center justify-right'>
          <div className='relative inline-block'>
            <Menu>
              {({ open }) => (
                <>
                  <Menu.Button className='inline-flex justify-right w-full transition duration-100 ease-in-out'>
                    <ViewGridIcon
                      title='View Settings'
                      className='w-5 h-5 text-customRed mx-auto cursor-pointer'
                      aria-hidden='true'
                    />
                  </Menu.Button>

                  <Transition
                    show={open}
                    enter='transition ease-out duration-75'
                    enterFrom='transform opacity-0 scale-95'
                    enterTo='transform opacity-100 scale-100'
                    leave='transition ease-in duration-75'
                    leaveFrom='transform opacity-100 scale-100'
                    leaveTo='transform opacity-0 scale-95'
                  >
                    <Menu.Items
                      static
                      className='absolute right-0 w-44 mt-1 origin-top-right bg-white border border-gray-200 divide-y divide-gray-100 rounded-md shadow-lg outline-none z-50'
                    >
                      <div className='py-2'>
                        <Menu.Item>
                          {({ active }) => (
                            <NavLink
                              to='/project-setting'
                              className={`${
                                active
                                  ? 'bg-gray-100 text-gray-900'
                                  : 'text-gray-700'
                              } flex justify-between w-full px-4 py-2 text-sm leading-5 text-left relative`}
                            >
                              <CogIcon
                                className='w-5 h-5 opacity-70 absolute'
                                aria-hidden='true'
                              />
                              <span className='ml-8'>Project settings</span>
                            </NavLink>
                          )}
                        </Menu.Item>
                        <Menu.Item>
                          {({ active }) => (
                            <NavLink
                              to='/support'
                              className={`${
                                active
                                  ? 'bg-gray-100 text-gray-900'
                                  : 'text-gray-700'
                              } flex justify-between w-full px-4 py-2 text-sm leading-5 text-left relative`}
                            >
                              <TrashIcon
                                className='w-5 h-5 opacity-70 absolute'
                                aria-hidden='true'
                              />
                              <span className='ml-8'>Move to trash</span>
                            </NavLink>
                          )}
                        </Menu.Item>
                      </div>
                    </Menu.Items>
                  </Transition>
                </>
              )}
            </Menu>
          </div>
        </div>
      </div>
    </>
  );
};

export default SettingsModal;
